import { useState } from 'react';
import './style.css';
import firebase from './firebaseConnection.js';

function App() {

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [cargo, setCargo] = useState('');
  const [nome, setNome] = useState(''); 

  const[user, setUser] = useState({});

  async function novoUsuario(){
    await firebase.auth().createUserWithEmailAndPassword(email, senha)
    .then(async(value)=>{

      await firebase.firestore().collection('users')
      .doc(value.user.uid)
      .set({
        nome: nome,
        cargo: cargo,
        status: true,
      })
      .then(()=>{
        setNome('');
        setCargo('');
        setEmail('');
        setSenha('');
      })

    })
    .catch((error)=>{
      if(error.code === 'auth/weak-password'){
        alert('Senha muito fraca!')
      }else if(error.code === 'auth/email-already-in-use'){
        alert('Esse email jÃ¡ existe!');
      }
    })
  }

  async function logout(){
    await firebase.auth().signOut();
    setUser({});
  }

  async function login(){
    await firebase.auth().signInWithEmailAndPassword(email, senha)
    .then(async(value)=>{
      await firebase.firestore().collection('users')
      .doc(value.user.uid)
      .get()
      .then((snapshot)=>{
        setUser({
          nome: snapshot.data().nome,
          cargo: snapshot.data().cargo,
          status: snapshot.data().status,
          email: value.user.email
        });

      })
    })
    .catch((error)=>{
      console.log('Erro ao fazer login'+ error)
    })
  }

  return (
    <div>
     <h1>Reactjs + Firebase :)</h1> <br/>

     <div className="container">
       <label>Nome</label>
       <input type="text" value={nome} onChange={ (e) => setNome(e.target.value)} /> <br/>

       <label>Cargo</label>
       <input type="text" value={cargo} onChange={ (e) => setCargo(e.target.value)} /> <br/>

       <label>Email</label>
       <input type="text" value={email} onChange={ (e) => setEmail(e.target.value)} /> <br/>


       <label>Senha</label>
       <input type="password" value={senha} onChange={ (e) => setSenha(e.target.value)} /> <br/>

       <button onClick={ login } >Fazer Login</button>
       <button onClick={ novoUsuario }>Cadastrar</button>
       <button onClick={ logout }>Sair da Conta!</button>
     </div>

     <hr/> <br/>
     {Object.keys(user).length > 0 &&(
       <div>
         <strong>OlÃ¡ </strong> {user.nome} <br/>
         <strong>Cargo: </strong> {user.cargo} <br/>
         <strong>Email: </strong> {user.email} <br/>
         <strong>Status: </strong> {user.status ? 'Ativo' : 'Desativado'} <br/>
         </div>
     )}

     </div>
  );
}

export default App;
