import { useState, useEffect, useReducer } from 'react';
import './style.css';
import firebase from './firebaseConnection.js';

function App() {
  const [idPost, setIdPost] = useState('');
  const [titulo, setTitulo] = useState('');
  const [autor, setAutor] = useState('');
  const [posts, setPosts] = useState([]);

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [user, setUser] = useState(false);
  const [userLogged, setUserLogged] = useState({});

  useEffect(()=>{
    async function loadPosts(){
      await firebase.firestore().collection('posts')
      .onSnapshot((doc)=>{
        let meusPosts = [];

        doc.forEach((item)=>{
          meusPosts.push({
            id: item.id,
            titulo: item.data().titulo,
            autor: item.data().autor,
          })
        });

        setPosts(meusPosts);

        })

      }

      loadPosts();

  }, []);

  useEffect(()=>{

  async function checkLogin(){
    await firebase.auth().onAuthStateChanged((user)=>{
    if(user){
      //se tem usuario logado entra aqui...
      setUser(true);
      setUserLogged({
        uid:user.uid,
        email: user.email
      })      
    }else{
      //nao possui nenhum usuario logado...
      setUser(false);
      setUserLogged({});
    }
  })
}

checkLogin();

  }, []);  


  async function handleAdd(){

    await firebase.firestore().collection('posts')
    .add({
      titulo: titulo,
      autor: autor,
    })
    .then(()=>{
      console.log('DADOS CADASTRADOS!');
      setTitulo('');
      setAutor('');
    })
    .catch((error)=>{
      console.log('GEROU ALGUM ERRO: ' + error);
    })

  }

  async function buscaPost(){
    // await firebase.firestore().collection('posts')
    // .doc('123')
    // .get()
    // .then((snapshot)=>{

    //   setTitulo(snapshot.data().titulo);
    //   setAutor(snapshot.data().autor);
    // })
    // .catch(()=>{
    //   console.log('DEU ALGUM ERRO')
    // })

    await firebase.firestore().collection('posts')
    .get()
    .then((snapshot)=>{
      let lista = [];

      snapshot.forEach((doc)=>{
        lista.push({
          id: doc.id,
          titulo: doc.data().titulo,
          autor: doc.data().autor
        })
      })

      setPosts(lista);


    })
    .catch(()=>{
      console.log('DEU ALGUM ERRO!');
    })
  }

  async function editarPost(){
    await firebase.firestore().collection('posts')
    .doc(idPost)
    .update({
      titulo: titulo,
      autor: autor
    })
    .then(()=>{
      console.log('DADOS ATUALIZADOS COM SUCESSO!');
      setIdPost('');
      setTitulo('');
      setAutor('');
    })
    .catch(()=>{
      console.log('ERRO AO ATUALIZAR!');
    })
  }

  async function excluirPost(id){
    await firebase.firestore().collection('posts').doc(id)
    .delete()
    .then(()=>{
      alert('ESTE POST FOI EXCLUIDO!')
    })
  }

  async function novoUsuario(){
    await firebase.auth().createUserWithEmailAndPassword(email, senha)
    .then((value)=>{
      console.log(value);
    })


    .catch((error)=>{
      if(error.code === 'auth/weak-password'){
        alert('Senha muito fraca!')
      }else if(error.code === 'auth/email-already-in-use'){
        alert('Esse email já existe!');
      }else if(error.code === 'auth/invalid-email'){
        alert('Esse email não é valido!')
      }
    })
  }

  async function logout(){
    await firebase.auth().signOut();
  }

  async function fazerLogin(){
    await firebase.auth().signInWithEmailAndPassword(email, senha)
    .then((value)=>{
      console.log(value.user);
    })
    .catch((error)=>{
      console.log('Erro ao fazer login' + error);
    })
  }

  return (
    <div>
     <h1>Reactjs + Firebase :)</h1> <br/>


     <div className="container">
       <label>Email</label>
       <input type="text" value={email} onChange={ (e) => setEmail(e.target.value)} /> <br/>

       <label>Senha</label>
       <input type="password" value={senha} onChange={ (e) => setSenha(e.target.value)} /> <br/>


       <button onClick={ novoUsuario }>Cadastrar</button>
       <button onClick={ logout }>Sair da Conta!</button>
     </div>


    </div>
  );
}

export default App;
